mapper.select:
select id, username, email, password
from member
where id = #{id}

select id, username, email, password
from member
where username = #{username}

select id, username, email, password
from member
where email = #{email}

mapper.update:
update member
set username = #{newUsername}
where id = #{id}

update member
set password = #{newPassword}
where id = #{id}
